import { useParams } from 'react-router-dom';

const ProductDetail = () => {

    // extracting route params
    const params = useParams();

    return (
        <section>
            <h1>Product detail</h1>
            <p>{params.productId}</p>
        </section>
    )
};

export default ProductDetail;
